package chapter01;

public class PrintTriangle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("*");
		System.out.println("**");
		System.out.println("****");
		System.out.println("*****");
		System.out.println("******");
		System.out.println("*******");
		
		boolean a = true;
		char b = 'A';  // char 字符变量 用单引号
		char c = 650;
		String d = "This is a dog"; // String 用双引号
		float i = 3.1f;  // float类似默认为double类型，不加f则报错
		double j = 2.25;
		
		/*
		 整数类型
		 byte -128~127, 
		 short 25555, 
		 int, 
		 long
		 浮点类型：float 单精度, double 双精度
		 */
		System.out.print("boolea="+a);
	}

}
